pub fn get(path: &str) -> &str{
    match path{
        "shutdown.html" => {
            return "<html><body><img style=\"vertical-align: middle;\" href=\"http://icons8.com\" src=\"data:image/png;base64,\" alt=\"\" /><span>Cock-a-doodle-doo! Bye bye!</span></body></html>";
        },
        "about.html" => {
            return "<html><body><img style=\"vertical-align: middle;\" href=\"http://icons8.com\" src=\"data:image/png;base64,\" alt=\"\" /><span>Hi!, I am Rooster!. I am a personal web server.</span></body></html>";
        }
        _ => {
            return "";     
        }
    }
}

